import React from "react";
import Productheader from "./productheader";
import fetch from "isomorphic-fetch";
import ProductShell from "./Product";

const productsEndpoint = "/allPhones";
const initialState = {
  products: [],
  //all our filters
  filtered: false,
  filteredArray: [],
  error: false
};

class Product extends React.Component {
  state = initialState;

  //didmount is invoked immediately after a component is mounted
  async componentDidMount() {
    try {
      const data = await fetch(productsEndpoint);
      const jsonData = await data.json();
      //Sets state to parsed json data
      this.setState({ products: jsonData }, () => {
        //logs to make sure state worked
        console.log(this.state.products);
      });
    } catch (err) {
      console.log(err);
      this.setState({ error: true });
    }
  }

  handleFilter = filter => {
    const allProducts = this.state.products.slice();

    if (filter == "no filter") {
      this.setState({
        filtered: false
      });
      return;
    }

    //if true keep it in the array if not lets get rid of it
    allProducts.filter(product => {
      return product.Names.includes(filter);
    });

    this.setState({
      filteredArray: allProducts,
      filtered: true
    });
  };

  render() {
    let products;
    if (this.state.filtered) {
      products = this.state.filteredArray.map(product => (
        <ProductShell
          image={product.URL}
          name={product.Names}
          price={product.Price}
        />
      ));
    } else {
      products = this.state.filteredArray.map(product => (
        <ProductShell
          image={product.URL}
          name={product.Names}
          price={product.Price}
        />
      ));
    }

    return (
      <React.Fragment>
        <Productheader />
        <div>
          <button onClick={() => this.handleFilter("no filter")}>
            No filter
          </button>
          <button onClick={() => this.handleFilter("iphone")}>Iphones</button>
          <button onClick={() => this.handleFilter("Samsung")}>Samsung</button>
          <button onClick={() => this.handleFilter("LG")}>LG</button>
        </div>
        {products}
      </React.Fragment>
      // <div>
      //   {this.state.products.map(e => {
      //     return (
      //       <div>
      //         <h1>{e["Names"]}</h1>
      //         <img src={e["URL"]}></img>
      //       </div>
      //     );
      //   })}
      // </div>
    );
  }
}

export default Product;
